def nbr_de_journalist
 puts "Il y a #{@tableaujournalistes.size.to_i} journalistes dans la liste."
end


def number_in_array
array_journalist_number = []
@tableaujournalistes.each do |handle|
       if handle.include?('0') || handle.include?('1') || handle.include?('2') || handle.include?('3') || handle.include?('4') || handle.include?('5') || handle.include?('6') || handle.include?('7') || handle.include?('8') || handle.include?('9')
array_journalist_number.push handle
       end
       end
       puts "il y a #{array_journalist_number.size.to_i} qui ont un chiffre dans leur pseudo twitter"
end

def search_aude
 aude = []
 @tableaujournalistes.map(&:downcase).each do |handle|
       if handle.include?('aude')
         aude.push handle
       end
       end
       puts "Il y a #{aude.size.to_i} qui ont Aude dans leur nom."
end

def search_maj
  cap = []
  @tableaujournalistes.each do |handle|
    if handle[1] =~ /[A-Z]/
      cap.push handle
    end
  end
  puts "Il y a #{cap.size.to_i} pseudos qui commence avec une majuscule."
end

def contain_maj
  maj = []
  @tableaujournalistes.each do |handle|
      if handle =~ /[A-Z]/
        maj.push handle
      end
    end
    puts "Il y a #{maj.size.to_i} pseudos qui contiennent des majuscules."
end

def search__
  space = []
  @tableaujournalistes.each do |handle|
      if handle =~ /[_]/
        space.push handle
      end
    end
    puts "Il y a #{space.size.to_i} noms qui contiennent des _."
end

def alph
